    public RegisterFrame() {	
    	super("Rejestracja");
    	
    	setVisible(true);
		setDefaultCloseOperation(JFrame.DISPOSE_ON_CLOSE);
		setSize(400, 250);
		setLocation(400, 250);
		
		JPanel fieldPanel = new JPanel();
		JPanel buttonPanel = new JPanel();
		JPanel labelPanel = new JPanel();
		JPanel infoPanel = new JPanel();
		JPanel mainPanel = new JPanel();
		
		mainPanel.setLayout(new BoxLayout(mainPanel, BoxLayout.PAGE_AXIS));
		
		GridLayout gl = new GridLayout(0, 2);
		gl.setHgap(5);
		fieldPanel.setLayout(gl);
		
		JLabel register = new JLabel("Register");
		JLabel info = new JLabel("       ");
		JLabel name = new JLabel("Name:");
		JLabel password = new JLabel("Password:");
		JLabel repPassword = new JLabel("Repeat Password:");
		JLabel mail = new JLabel("E-Mail adress:");
		
		info.setName("info");
		
		register.setFont(new Font("serif", Font.BOLD, 14));
		info.setForeground(Color.red);
		
		JTextField nameField = new JTextField();
		nameField.setName("name");
		JPasswordField passwordField = new JPasswordField();
		passwordField.setName("pass");
		JPasswordField repPasswordField = new JPasswordField();
		repPasswordField.setName("repPass");
		JTextField mailField = new JTextField();
		mailField.setName("mail");
		
		JButton registerButton = new JButton("Register");
		registerButton.addActionListener(this);
		
		labelPanel.add(register);
		
		infoPanel.add(info);

		buttonPanel.add(registerButton);

		fieldPanel.add(name);
		fieldPanel.add(nameField);
		fieldPanel.add(password);
		fieldPanel.add(passwordField);
		fieldPanel.add(repPassword);
		fieldPanel.add(repPasswordField);
		fieldPanel.add(mail);
		fieldPanel.add(mailField);
		
		mainPanel.add(labelPanel);
		mainPanel.add(infoPanel);
		mainPanel.add(fieldPanel);
		mainPanel.add(buttonPanel);
		
		this.getContentPane().add(mainPanel, BorderLayout.CENTER);
		components = Util.getAllComponents(this.getContentPane());
    }
    
